mapper.update:
UPDATE MBR_INFO
   SET LGN_FAIL_CNT = LGN_FAIL_CNT + 1
	 , LTST_LGN_FAIL_DT = SYSDATE 
	 , LTST_LGN_IP = #{ip}
 WHERE EMIL_ADDR = #{emilAddr}

UPDATE MBR_INFO 
   SET LGN_FAIL_CNT = 0
	 , LTST_LGN_SCCSS_DT = SYSDATE 
     , LTST_LGN_FAIL_DT = NULL 
     , LTST_LGN_IP = #{ip}
 WHERE EMIL_ADDR = #{emilAddr}

