mapper.select:
SELECT * FROM ( SELECT ROWNUM as RNUM , S.* FROM ( SELECT MBR_PRTFL_ID , MBR_PRTFL_TTL , MBR_PRTFL_TEXT , EMIL_ADDR FROM MBR_PRTFL WHERE EMIL_ADDR IN #{searchIdParam}
(SELECT EMIL_ADDR FROM MBR_INFO WHERE CMP_ID = #{searchIdParam})
ORDER BY MBR_PRTFL_ID DESC ) S WHERE ROWNUM <= (#{currPageNo} + 1)*#{exposureListSize} ) WHERE RNUM >= #{currPageNo} * #{exposureListSize} + 1

SELECT CMP_ID 
  FROM MBR_INFO  
 WHERE EMIL_ADDR = #{_parameter}
