mapper.update:
UPDATE PJ_INFO
SET PJ_STT = #{pjStt}
WHERE PJ_ID =#{pjId}

     UPDATE PJ_CMMNT
		SET 
		    CMMNT_CNTNT= #{cmmntCntnt}
		  , LST_MOD_DT= SYSDATE
	  WHERE PJ_CMMNT_ID= #{pjCmmntId}

UPDATE PJ_INFO
SET VIEW_CNT = VIEW_CNT + 1
WHERE PJ_ID = #{_parameterType}

UPDATE PJ_INDSTR
SET INDSTR_ID = #{indstrId}
WHERE PJ_ID = #{pjId}

UPDATE PJ_APPLY
SET PJ_APPLY_TTL = #{pjApplyTtl},
PJ_APPLY_DESC = #{pjApplyDesc},
PJ_APPLY_MDFY_DT = SYSDATE
WHERE PJ_APPLY_ID = #{pjApplyId}
AND EMIL_ADDR = #{emilAddr}

mapper.select:
SELECT p.PJ_ID , p.ORDR_ID , p.OBTN_ID , p.PJ_TTL , p.RGSTR_DT , p.STRT_DT , p.END_DT , p.CNTRCT_ACCNT , p.PJ_DESC , p.IS_DLT , p.DLT_DT , p.LST_MOD_DT , p.IS_RCRUT_ADD , p.PJ_STT , p.VIEW_CNT , p.PJ_RCRUT_CNT , p.PJ_RCRUT_STRT_DT , p.PJ_RCRUT_END_DT FROM PJ_INFO p WHERE OBTN_ID IN
(SELECT EMIL_ADDR FROM MBR_INFO WHERE CMP_ID =( SELECT CMP_ID FROM MBR_INFO WHERE EMIL_ADDR =#{emilAddr}))
#{emilAddr}

SELECT PA.PJ_APPLY_ID, PA.EMIL_ADDR, TO_CHAR(PA.PJ_APPLY_RGSTR_DT, 'YYYY-MM-DD') AS PJ_APPLY_RGSTR_DT, PA.PJ_APPLY_TTL, PA.PJ_APPLY_DESC, TO_CHAR(PA.PJ_APPLY_MDFY_DT, 'YYYY-MM-DD') AS PJ_APPLY_MDFY_DT, PAA.PJ_APPLY_ATT_ID, PAA.PJ_APPLY_ID, PAA.PJ_APPLY_ATT_URL, PAA.PJ_APPLY_ATT_URL_NONREAD, PI.PJ_ID, PI.ORDR_ID, PI.OBTN_ID, PI.PJ_TTL, TO_CHAR(PI.RGSTR_DT, 'YYYY-MM-DD') AS RGSTR_DT, TO_CHAR(PI.STRT_DT, 'YYYY-MM-DD') AS STRT_DT, TO_CHAR(PI.END_DT, 'YYYY-MM-DD') AS END_DT, PI.CNTRCT_ACCNT, PI.PJ_DESC, PI.IS_DLT, TO_CHAR(PI.DLT_DT, 'YYYY-MM-DD') AS DLT_DT, PI.LST_MOD_DT, PI.IS_RCRUT_ADD, PI.PJ_STT, PI.VIEW_CNT, PI.PJ_RCRUT_CNT, TO_CHAR(PI.PJ_RCRUT_STRT_DT, 'YYYY-MM-DD') AS PJ_RCRUT_STRT_DT, TO_CHAR(PI.PJ_RCRUT_END_DT, 'YYYY-MM-DD') AS PJ_RCRUT_END_DT, P.PJ_INDSTR_ID, P.SMJR_ID, II.INDSTR_ID, II.INDSTR_NM, II.UPPR_INDSTR_ID, PPS.PJ_PRM_STK_ID, PRM.PRM_STK_ID, PRM.PRM_STK FROM PJ_APPLY PA JOIN PJ_INFO PI ON PA.PJ_ID = PI.PJ_ID JOIN PJ_INDSTR P ON PI.PJ_ID = P.PJ_ID LEFT JOIN PJ_APPLY_ATT PAA ON PAA.PJ_APPLY_ID = PA.PJ_APPLY_ID JOIN INDSTR_INFO II ON II.INDSTR_ID = P.SMJR_ID JOIN PJ_PRM_STK PPS ON PPS.PJ_ID = P.PJ_ID JOIN PRM_STK PRM ON PRM.PRM_STK_ID = PPS.PRM_STK_ID WHERE PI.PJ_ID IN ( SELECT PJ_ID FROM PJ_INFO WHERE PJ_STT = 0 OR PJ_STT = 3)
AND PA.EMIL_ADDR IN (SELECT EMIL_ADDR FROM MBR_INFO WHERE CMP_ID = (SELECT CMP_ID FROM MBR_INFO WHERE EMIL_ADDR = #{emilAddr}))
AND PA.EMIL_ADDR = #{emilAddr}
